const SetEnemyBoard = (setEnemyBoard) => {
    // const BOARD_SIZE = 10;

    // Define five different enemy board layouts
    const enemyBoards = [
        [  // Layout 1
            "C", "C", "C", "C", "C", null, null, null, null, null,
            null, null, null, null, null, "B", "B", "B", "B", null,
            null, "S", "S", "S", null, null, null, null, null, null,
            null, null, null, null, null, "D", "D", null, null, null,
            null, null, null, "S", "S", "S", null, null, null, null,
            null, "B", "B", "B", "B", null, null, null, null, null,
            null, null, null, null, "C", "C", "C", "C", "C", null,
            null, null, null, null, null, null, null, null, null, null,
            null, "D", "D", null, null, null, null, null, null, null,
            null, null, null, null, null, null, null, null, null, null
        ],
        [  // Layout 2
            null, null, null, null, null, null, "S", "S", "S", null,
            null, null, "B", "B", "B", "B", null, null, null, null,
            null, null, null, null, null, null, null, "C", "C", "C",
            "C", "C", null, null, null, null, null, null, null, null,
            null, null, "D", "D", null, null, null, null, null, null,
            null, "S", "S", "S", null, null, null, null, null, null,
            null, null, null, null, null, "B", "B", "B", "B", null,
            null, null, null, null, null, null, null, "D", "D", null,
            null, null, null, null, null, null, null, null, null, null,
            null, null, null, null, null, null, null, null, null, null
        ],
        // Add three more layouts...
    ];

    // Select a random enemy board layout
    const selectedBoard = enemyBoards[Math.floor(Math.random() * enemyBoards.length)];

    // Update enemy board state
    setEnemyBoard(selectedBoard);
};

export default SetEnemyBoard;
