import React, { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";

const shipsData = [
    { id: "Carrier 5x1", size: 5 },
    { id: "Battleship 4x1", size: 4 },
    { id: "Cruiser 3x1", size: 3 },
    { id: "Submarine 3x1", size: 3 },
    { id: "Destroyer 2x1", size: 2 },
];

const BattleshipGame = () => {
    const [ships, setShips] = useState(shipsData);
    const [playerBoard, setPlayerBoard] = useState(Array(10).fill(Array(10).fill(null)));
    const [enemyBoard, setEnemyBoard] = useState(Array(10).fill(Array(10).fill(null)));
    const [timer, setTimer] = useState(0);
    const [gameStarted, setGameStarted] = useState(false);

    useEffect(() => {
        let interval;
        if (gameStarted) {
            interval = setInterval(() => {
                setTimer((prev) => prev + 1);
            }, 1000);
        }
        return () => clearInterval(interval);
    }, [gameStarted]);

    const handleReset = () => {
        setShips(shipsData);
        setPlayerBoard(Array(10).fill(Array(10).fill(null)));
        setEnemyBoard(Array(10).fill(Array(10).fill(null)));
        setTimer(0);
        setGameStarted(false);
    };

    return (
        <div className="p-4">
            <h1 className="text-xl font-bold mb-2">Battleship</h1>
            <div className="mb-4">Timer: {timer}s</div>
            <Button onClick={handleReset}>Reset Game</Button>
            <div className="mt-4">
                <h2 className="text-lg font-bold">Available Ships</h2>
                <ul>
                    {ships.map((ship) => (
                        <li key={ship.id}>{ship.id}</li>
                    ))}
                </ul>
            </div>
            <div className="grid grid-cols-2 gap-4 mt-4">
                <div>
                    <h2 className="text-lg font-bold">Player Board</h2>
                    <div className="grid grid-cols-10 gap-1">
                        {playerBoard.map((row, rowIndex) =>
                            row.map((cell, colIndex) => (
                                <div key={`${rowIndex}-${colIndex}`} className="w-8 h-8 border"></div>
                            ))
                        )}
                    </div>
                </div>
                <div>
                    <h2 className="text-lg font-bold">Enemy Board</h2>
                    <div className="grid grid-cols-10 gap-1">
                        {enemyBoard.map((row, rowIndex) =>
                            row.map((cell, colIndex) => (
                                <div key={`${rowIndex}-${colIndex}`} className="w-8 h-8 border"></div>
                            ))
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default BattleshipGame;
