import React, { useState, useEffect } from "react";

const BOARD_SIZE = 10;
const SHIPS = [5, 4, 3, 3, 2];

const Play = () => {
    const [playerBoard, setPlayerBoard] = useState(
        Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(null))
    );
    const [enemyBoard, setEnemyBoard] = useState(
        Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(null))
    );
    const [placedShips, setPlacedShips] = useState([]);
    const [isHorizontal, setIsHorizontal] = useState(true);
    const [gameStarted, setGameStarted] = useState(false);
    const [timer, setTimer] = useState(0);
    const [gameOver, setGameOver] = useState(false);

    useEffect(() => {
        let interval;
        if (gameStarted && !gameOver) {
            interval = setInterval(() => setTimer((t) => t + 1), 1000);
        }
        return () => clearInterval(interval);
    }, [gameStarted, gameOver]);

    const placeShip = (row, col, size) => {
        if (gameStarted) return;
        let newBoard = [...playerBoard];
        let newPlacedShips = [...placedShips];

        for (let i = 0; i < size; i++) {
            if (isHorizontal) {
                if (col + size > BOARD_SIZE) return;
                newBoard[row][col + i] = "S";
            } else {
                if (row + size > BOARD_SIZE) return;
                newBoard[row + i][col] = "S";
            }
        }

        newPlacedShips.push({ row, col, size, isHorizontal });
        setPlayerBoard(newBoard);
        setPlacedShips(newPlacedShips);
    };

    const attack = (row, col) => {
        if (!gameStarted) setGameStarted(true);
        if (gameOver || enemyBoard[row][col]) return;
        let newBoard = [...enemyBoard];
        let hit = Math.random() > 0.5 ? "H" : "M"; // Random hit or miss
        newBoard[row][col] = hit;
        setEnemyBoard(newBoard);
        if (!newBoard.flat().includes(null)) setGameOver(true);
    };

    const resetGame = () => {
        setPlayerBoard(Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(null)));
        setEnemyBoard(Array(BOARD_SIZE).fill(null).map(() => Array(BOARD_SIZE).fill(null)));
        setPlacedShips([]);
        setGameStarted(false);
        setGameOver(false);
        setTimer(0);
    };

    return (
        <div className="flex flex-col items-center p-4">
            <h1 className="text-2xl font-bold mb-4">Battleship</h1>
            <p>Time: {timer} seconds</p>
            <button onClick={() => setIsHorizontal(!isHorizontal)} className="mb-2 p-2 bg-blue-500 text-white rounded">
                Rotate Ship ({isHorizontal ? "Horizontal" : "Vertical"})
            </button>
            <div className="flex gap-8">
                <div>
                    <h2 className="text-lg font-bold">Your Board</h2>
                    <div className="grid grid-cols-10 gap-1 border p-2">
                        {playerBoard.map((row, rIdx) =>
                            row.map((cell, cIdx) => (
                                <div
                                    key={`${rIdx}-${cIdx}`}
                                    className={`w-8 h-8 border flex items-center justify-center ${cell === "S" ? "bg-gray-500" : "bg-blue-200"}`}
                                    onClick={() => placeShip(rIdx, cIdx, SHIPS[placedShips.length])}
                                ></div>
                            ))
                        )}
                    </div>
                </div>
                <div>
                    <h2 className="text-lg font-bold">Enemy Board</h2>
                    <div className="grid grid-cols-10 gap-1 border p-2">
                        {enemyBoard.map((row, rIdx) =>
                            row.map((cell, cIdx) => (
                                <div
                                    key={`${rIdx}-${cIdx}`}
                                    className={`w-8 h-8 border flex items-center justify-center cursor-pointer ${cell === "H" ? "bg-red-500" : cell === "M" ? "bg-gray-300" : "bg-blue-400"}`}
                                    onClick={() => attack(rIdx, cIdx)}
                                >
                                    {cell === "H" ? "✔" : cell === "M" ? "✖" : ""}
                                </div>
                            ))
                        )}
                    </div>
                </div>
            </div>
            <button onClick={resetGame} className="mt-4 p-2 bg-red-500 text-white rounded">Reset Game</button>
            {gameOver && <p className="text-xl font-bold text-red-600 mt-4">Game Over!</p>}
        </div>
    );
};

export default Play;
