import { Link } from "react-router-dom";
// import Navbar from '../../components/Navbar';
import '../../styles/global.css';
import '../../styles/game.css';
import Footer from '../../components/Footer';

function Game() {
    return (
        <div className="game">
            <Navbar />

            <main className="main">
                <header>
                    <h1>Battleship, The Game</h1>
                    <p>Select a game mode to start playing!</p>
                </header>

                <div className="main-container">
                    {/* Easy Mode */}
                    <div className="game-mode">
                        <h2>Easy Mode</h2>
                        <p>For a more relaxed gameplay experience.</p>
                        <Link to="/game/easy" className="btn">Play Easy</Link>
                    </div>

                    {/* Normal Mode */}
                    <div className="game-mode">
                        <h2>Normal Mode</h2>
                        <p>Challenge yourself with standard rules.</p>
                        <Link to="/game/normal" className="btn">Play Normal</Link>
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
}

export default Game;
