import Navbar from '../components/Navbar';
import '../styles/global.css';
import '../styles/sample.css';
import Footer from '../components/Footer';

function Play() {
    return (
        <div className="play">
            <Navbar />

            <main>
                <header>
                    <h1>Battleship Sample Game</h1>
                </header>

                {/* Timer and Restart Button */}
                <h3>
                    {/* TODO -- UPDATE THIS TO START ON FIRST PLAY*/}
                    Timer: <span className="red-text">00:05:32 </span>
                    <button className="restart-button">Restart</button>
                </h3>

                {/* Available Ships Box */}
                <div className="available-ships">
                    <div className="ship" draggable="true">5x1 Ship</div>
                    <div className="ship" draggable="true">4x1 Ship</div>
                    <div className="ship" draggable="true">3x1 Ship</div>
                    <div className="ship" draggable="true">3x1 Ship</div>
                    <div className="ship" draggable="true">2x1 Ship</div>
                </div>

                {/* Player Board (10x10 Grid) */}
                <div className="player-board">
                    {/* Grid cells will go here */}
                    {Array(100).fill(null).map((_, index) => (
                        <div key={index} className="board-cell"></div>
                    ))}
                </div>
            </main>

            <Footer />
        </div>
    );
}

export default Play;
